function download_retry() {
    while true; do
        echo ${1}
        wget -T 15 --continue -O "${1}" "${2}" && break
    done
}

for c in "${!files_to_download[@]}"; do
    echo ${c}
    download_retry "${c}" "${files_to_download[$c]}"
done
